import { tool } from "ai"
import { z } from "zod"

import { WebSearchItem, WebSearchSchema } from "./schema"

export const webSearchBraveTool = tool({
  name: "websearch-brave",
  description:
    "Search the web using the Brave Search API. Requires BRAVE_SEARCH_API_KEY.",
  inputSchema: z.object({
    query: z.string().min(1),
    limit: z.number().min(1).max(20).default(5),
    lang: z.string().optional(),
    country: z.string().optional(),
  }),
  outputSchema: WebSearchSchema,
  execute: async ({ query, limit, lang, country }) => {
    const braveToken = process.env.BRAVE_SEARCH_API_KEY
    if (!braveToken) {
      throw new Error(
        "BRAVE_SEARCH_API_KEY is required to use the Brave websearch tool"
      )
    }

    const params = new URLSearchParams({
      q: query,
      count: String(limit),
    })
    if (country) params.set("country", country)
    if (lang) params.set("search_lang", lang)

    const url = `https://api.search.brave.com/res/v1/web/search?${params.toString()}`
    const res = await fetch(url, {
      headers: {
        Accept: "application/json",
        "X-Subscription-Token": braveToken,
      },
    })

    if (!res.ok) throw new Error(`Brave API failed: ${res.status}`)

    const data = BraveSearchSchema.parse(await res.json())

    const items = Array.isArray(data.web?.results)
      ? data.web!.results!
      : Array.isArray(data.results)
        ? data.results!
        : []

    const results: WebSearchItem[] = items
      .slice(0, limit)
      .map((r: BraveWebResult) => {
        const title = r.title || r.url || "Untitled"
        const url = r.url || ""
        let source: string | undefined
        try {
          source =
            r.profile?.long_name || (url ? new URL(url).hostname : undefined)
        } catch {
          source = r.profile?.long_name || undefined
        }
        return {
          title,
          url,
          snippet: r.description || undefined,
          source: source || "Brave",
        }
      })
      .filter((r: WebSearchItem) => !!r.url)

    return { query, results }
  },
})

export default webSearchBraveTool

export const BraveProfileSchema = z
  .object({
    name: z.string().optional(),
    url: z.string().url().optional(),
    long_name: z.string().optional(),
    img: z.string().url().optional(),
  })
  .passthrough()

export const BraveWebResultSchema = z
  .object({
    title: z.string().optional(),
    url: z.string().url().optional(),
    description: z.string().optional(),
    profile: BraveProfileSchema.optional(),
  })
  .passthrough()

export const BraveWebSchema = z
  .object({
    results: z.array(BraveWebResultSchema).optional(),
  })
  .passthrough()

export const BraveSearchSchema = z
  .object({
    type: z.string().optional(),
    web: BraveWebSchema.optional(),
    results: z.array(BraveWebResultSchema).optional(),
  })
  .passthrough()

export type BraveWebResult = z.infer<typeof BraveWebResultSchema>

"use client"

import * as React from "react"
import type { WebSearchItem, WebSearchResult } from "./schema"
import { ToolUIPart } from "ai"
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/registry/ai-tools/ui/card"

export function WebSearchList(part: ToolUIPart) {
  const { query, results } = part.output as WebSearchResult
  return (
    <Card className="w-full max-w-xl">
      <CardHeader>
        <CardTitle>Web Search</CardTitle>
        <CardDescription>Query: {query}</CardDescription>
      </CardHeader>
      <CardContent>
        <ul className="space-y-3">
          {results.map((r: WebSearchItem) => (
            <li key={r.url} className="rounded-md bg-muted p-3">
              <a
                href={r.url}
                className="font-medium hover:underline"
                target="_blank"
                rel="noreferrer"
              >
                {r.title}
              </a>
              {r.source ? (
                <span className="ml-2 text-xs text-muted-foreground">
                  ({r.source})
                </span>
              ) : null}
              {r.snippet ? (
                <div className="text-xs text-muted-foreground mt-1">
                  {r.snippet}
                </div>
              ) : null}
            </li>
          ))}
        </ul>
      </CardContent>
    </Card>
  )
}

export default WebSearchList

import { z } from "zod"

export const WebSearchItemSchema = z.object({
  title: z.string(),
  url: z.string().url(),
  snippet: z.string().optional(),
  source: z.string().optional(),
})

export const WebSearchSchema = z.object({
  query: z.string(),
  results: z.array(WebSearchItemSchema),
})

export type WebSearchItem = z.infer<typeof WebSearchItemSchema>
export type WebSearchResult = z.infer<typeof WebSearchSchema>

import * as React from "react"

import { cn } from "@/lib/utils"

function Card({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card"
      className={cn(
        "bg-card text-card-foreground flex flex-col gap-6 rounded-xl border py-6",
        className
      )}
      {...props}
    />
  )
}

function CardHeader({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card-header"
      className={cn(
        "@container/card-header grid auto-rows-min grid-rows-[auto_auto] items-start gap-1.5 px-6 has-data-[slot=card-action]:grid-cols-[1fr_auto] [.border-b]:pb-6",
        className
      )}
      {...props}
    />
  )
}

function CardTitle({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card-title"
      className={cn(
        "leading-none tracking-tight text-lg font-semibold",
        className
      )}
      {...props}
    />
  )
}

function CardDescription({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card-description"
      className={cn("text-muted-foreground text-sm", className)}
      {...props}
    />
  )
}

function CardAction({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card-action"
      className={cn(
        "col-start-2 row-span-2 row-start-1 self-start justify-self-end",
        className
      )}
      {...props}
    />
  )
}

function CardContent({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card-content"
      className={cn("px-6", className)}
      {...props}
    />
  )
}

function CardFooter({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card-footer"
      className={cn("flex items-center px-6 [.border-t]:pt-6", className)}
      {...props}
    />
  )
}

export {
  Card,
  CardHeader,
  CardFooter,
  CardTitle,
  CardAction,
  CardDescription,
  CardContent,
}
