import { tool } from "ai"
import { z } from "zod"

import { WebSearchSchema, WebSearchItem } from "./schema"

export const webSearchDDGTool = tool({
  name: "websearch-ddg",
  description:
    "Search the web using DuckDuckGo Instant Answer (no key required).",
  inputSchema: z.object({
    query: z.string().min(1),
    limit: z.number().min(1).max(20).default(5),
    lang: z.string().optional(),
  }),
  outputSchema: WebSearchSchema,
  execute: async ({ query, limit, lang }) => {
    const url = `https://api.duckduckgo.com/?${new URLSearchParams({
      q: query,
      format: "json",
      no_redirect: "1",
      no_html: "1",
      t: "ai-tools-registry",
      kl: lang ? `${lang}-en` : "",
    }).toString()}`

    const res = await fetch(url)
    if (!res.ok) throw new Error(`DuckDuckGo API failed: ${res.status}`)
    const data = DDGResponseSchema.parse(await res.json())

    const flatten = (
      items: DDGRelated[] = [],
      acc: DDGTopic[] = []
    ): DDGTopic[] => {
      for (const it of items) {
        if ((it as any).Topics) acc = flatten((it as any).Topics, acc)
        else if ((it as any).FirstURL && (it as any).Text) acc.push(it as any)
      }
      return acc
    }

    const related = flatten(data.RelatedTopics)
    const results: WebSearchItem[] = (related || [])
      .slice(0, limit)
      .map((r) => {
        let hostname: string | undefined
        try {
          hostname = new URL(r.FirstURL).hostname
        } catch {
          hostname = undefined
        }
        return {
          title: r.Text,
          url: r.FirstURL,
          snippet: undefined,
          source: hostname || "DuckDuckGo",
        }
      })

    return { query, results }
  },
})

export default webSearchDDGTool

export const DDGTopicSchema = z
  .object({
    FirstURL: z.string().url(),
    Text: z.string(),
  })
  .passthrough()

export type DDGTopic = z.infer<typeof DDGTopicSchema>

export const DDGRelatedGroupSchema = z
  .object({
    Name: z.string().optional(),
    Topics: z.array(DDGTopicSchema),
  })
  .passthrough()

export type DDGRelatedGroup = z.infer<typeof DDGRelatedGroupSchema>

export const DDGRelatedSchema = z.union([DDGTopicSchema, DDGRelatedGroupSchema])
export type DDGRelated = z.infer<typeof DDGRelatedSchema>

export const DDGResponseSchema = z
  .object({
    Results: z.array(DDGTopicSchema).optional(),
    RelatedTopics: z.array(DDGRelatedSchema).optional(),
  })
  .passthrough()

export type DDGResponse = z.infer<typeof DDGResponseSchema>

"use client"

import * as React from "react"
import type { WebSearchItem, WebSearchResult } from "./schema"
import { ToolUIPart } from "ai"
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/registry/ai-tools/ui/card"

export function WebSearchList(part: ToolUIPart) {
  const { query, results } = part.output as WebSearchResult
  return (
    <Card className="w-full max-w-xl">
      <CardHeader>
        <CardTitle>Web Search</CardTitle>
        <CardDescription>Query: {query}</CardDescription>
      </CardHeader>
      <CardContent>
        <ul className="space-y-3">
          {results.map((r: WebSearchItem) => (
            <li key={r.url} className="rounded-md bg-muted p-3">
              <a
                href={r.url}
                className="font-medium hover:underline"
                target="_blank"
                rel="noreferrer"
              >
                {r.title}
              </a>
              {r.source ? (
                <span className="ml-2 text-xs text-muted-foreground">
                  ({r.source})
                </span>
              ) : null}
              {r.snippet ? (
                <div className="text-xs text-muted-foreground mt-1">
                  {r.snippet}
                </div>
              ) : null}
            </li>
          ))}
        </ul>
      </CardContent>
    </Card>
  )
}

export default WebSearchList

import { z } from "zod"

export const WebSearchItemSchema = z.object({
  title: z.string(),
  url: z.string().url(),
  snippet: z.string().optional(),
  source: z.string().optional(),
})

export const WebSearchSchema = z.object({
  query: z.string(),
  results: z.array(WebSearchItemSchema),
})

export type WebSearchItem = z.infer<typeof WebSearchItemSchema>
export type WebSearchResult = z.infer<typeof WebSearchSchema>

import * as React from "react"

import { cn } from "@/lib/utils"

function Card({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card"
      className={cn(
        "bg-card text-card-foreground flex flex-col gap-6 rounded-xl border py-6",
        className
      )}
      {...props}
    />
  )
}

function CardHeader({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card-header"
      className={cn(
        "@container/card-header grid auto-rows-min grid-rows-[auto_auto] items-start gap-1.5 px-6 has-data-[slot=card-action]:grid-cols-[1fr_auto] [.border-b]:pb-6",
        className
      )}
      {...props}
    />
  )
}

function CardTitle({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card-title"
      className={cn(
        "leading-none tracking-tight text-lg font-semibold",
        className
      )}
      {...props}
    />
  )
}

function CardDescription({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card-description"
      className={cn("text-muted-foreground text-sm", className)}
      {...props}
    />
  )
}

function CardAction({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card-action"
      className={cn(
        "col-start-2 row-span-2 row-start-1 self-start justify-self-end",
        className
      )}
      {...props}
    />
  )
}

function CardContent({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card-content"
      className={cn("px-6", className)}
      {...props}
    />
  )
}

function CardFooter({ className, ...props }: React.ComponentProps<"div">) {
  return (
    <div
      data-slot="card-footer"
      className={cn("flex items-center px-6 [.border-t]:pt-6", className)}
      {...props}
    />
  )
}

export {
  Card,
  CardHeader,
  CardFooter,
  CardTitle,
  CardAction,
  CardDescription,
  CardContent,
}
